var React = require('react/addons'),
    Link = require('react-router').Link,
    GrassRootsRadial = require('hackoregon/components/campaign/GrassRootsRadial'),
    InDistrictRadial = require('hackoregon/components/campaign/InDistrictRadial');

var CampaignTile = React.createClass({
    getPartyClasses: function() {
        return 'col-xs-2 party-' + ((this.props.campaign.party || '').toLowerCase());
    },
    render: function() {
        return (
            <div className="campaign-tile">
                <h2 className="text-center candidate-name">
                    <Link to="campaign" params={{campaignId: this.props.campaign.filer_id}}>{this.props.campaign.candidate_name}</Link>
                </h2>
                <div className="text-center race">{this.props.campaign.race}</div>
                <div className="portrait col-xs-2 col-xs-offset-2 col-sm-offset-0">
                    <img src="/images/icons/genderless.svg" />
                </div>
                <div className="col-xs-2">{this.props.campaign.party}</div>
                <div className="row">
                    <div className={this.getPartyClasses()}></div>
                </div>
                <div className="total">{this.props.campaign.total}</div>
                <div>Total Money Raised</div>
                <h5>Funding Sources</h5>
                <div>
                    <GrassRootsRadial 
                        percent={this.props.campaign.grassroots || 0 }
                    />
                    <InDistrictRadial 
                        percent={this.props.campaign.instate || 0}
                    />
                </div>
            </div>

        )
    }
});

module.exports = CampaignTile;