var React = require('react/addons');


var Raised = React.createClass({
    propTypes: {
        amount: React.PropTypes.string
    },
    getDefaultProps: function() {
        return {
            amount: '0'
        }
    },
    render: function() {
        return (
            <div>
                <div className="col-sm-4 total total-raised">
                    <strong>Raised</strong>
                </div>
                <div className="col-sm-8">
                    <div className="total total-raised">{this.props.amount}</div>
                </div>
            </div>
        )
    }
});

module.exports = Raised;