var React = require('react/addons');


var Spent = React.createClass({
    propTypes: {
        amount: React.PropTypes.number
    },
    getDefaultProps: function() {
        return {
            amount: 0
        }
    },
    render: function() {
        return (
            <div>
                <div className="total total-spent">
                    <strong>Spent</strong>
                </div>
                <div className="">
                    <div className="total total-spent">{this.props.amount}</div>
                </div>
            </div>
        )
    }
});

module.exports = Spent;